mapper.select:
select Member_Email memberEmail
	  ,Member_Password memberPassword
	  ,Member_Name memberName
from tbl_Member
where Member_Email = #{memberEmail}
  and Member_Password = #{memberPassword}

mapper.update:
update tbl_Member
set Session_Key = #{sessionID}
   ,Session_Limit = #{next}
where Member_Email = #{memberEmail}

