select Member_Email memberEmail 
from tbl_Member
where Member_Email = #{memberEmail}

mapper.update:
update tbl_Member
set Member_Password = #{memberPassword}
   ,Member_Name = #{memberName}
   ,Member_Birthday = #{memberBirthday}
where Member_Email = #{memberEmail}
